        public void ShouldVerifyCanonicalGeneratorsProperly(string hash, string pHex, string qHex, string seedHex, string indexHex, string gHex, bool success)
        {
            var p = new BitString(pHex).ToPositiveBigInteger();
            var q = new BitString(qHex).ToPositiveBigInteger();
            var g = new BitString(gHex).ToPositiveBigInteger();

            var fullDomainSeed = new BitString(seedHex);
            var seedLen = fullDomainSeed.BitLength / 3;
            var firstSeed = fullDomainSeed.MSBSubstring(0, seedLen).ToPositiveBigInteger();
            var pSeed = fullDomainSeed.MSBSubstring(seedLen, seedLen).ToPositiveBigInteger();
            var qSeed = fullDomainSeed.MSBSubstring(2 * seedLen, seedLen).ToPositiveBigInteger();

            var domainSeed = new DomainSeed(firstSeed, pSeed, qSeed);
            var index = new BitString(indexHex);

            var sha = GetSha(hash);

            var subject = new CanonicalGeneratorGeneratorValidator(sha);

            var result = subject.Validate(p, q, g, domainSeed, index);
            Assert.AreEqual(success, result.Success, result.ErrorMessage);
        }

        private ISha GetSha(string hash)
        {
            var mapping = AlgorithmSpecificationToDomainMapping.GetMappingFromAlgorithm(hash);
            return _shaFactory.GetShaInstance(new HashFunction(mapping.shaMode, mapping.shaDigestSize));
        }
