        public void ShouldVerifySignaturesCorrectly(string hash, string pHex, string qHex, string gHex, string msgHex, string yHex, string randHex, string sigHex, bool success)
        {
            var p = new BitString(pHex).ToPositiveBigInteger();
            var q = new BitString(qHex).ToPositiveBigInteger();
            var g = new BitString(gHex).ToPositiveBigInteger();
            var y = new BitString(yHex).ToPositiveBigInteger();
            var message = new BitString(msgHex);
            var sig = new BitString(sigHex).ToPositiveBigInteger();
            var rand = new BitString(randHex).ToPositiveBigInteger();

            var domainParams = new FfcDomainParameters(p, q, g);
            var keyPair = new FfcKeyPair(y);
            var signature = new FfcSignature(rand, sig);
            var dsa = new FfcDsa(GetSha(hash));

            var result = dsa.Verify(domainParams, keyPair, message, signature);

            Assert.AreEqual(success, result.Success, result.ErrorMessage);
        }

