        public void ShouldVerifyPQProperly(string hash, string pHex, string qHex, string seedHex, string pSeedHex, string qSeedHex, int pCount, int qCount, bool success)
        {
            var sha = GetSha(hash);

            var p = new BitString(pHex).ToPositiveBigInteger();
            var q = new BitString(qHex).ToPositiveBigInteger();

            var firstSeed = new BitString(seedHex).ToPositiveBigInteger();
            var pSeed = new BitString(pSeedHex).ToPositiveBigInteger();
            var qSeed = new BitString(qSeedHex).ToPositiveBigInteger();
            var seed = new DomainSeed(firstSeed, pSeed, qSeed);

            var count = new Counter(pCount, qCount);

            var subject = new ProvablePQGeneratorValidator(sha);

            var result = subject.Validate(p, q, seed, count);

            Assert.AreEqual(success, result.Success, result.ErrorMessage);
        }

        private ISha GetSha(string hash)
        {
            var mapping = AlgorithmSpecificationToDomainMapping.GetMappingFromAlgorithm(hash);
            return _shaFactory.GetShaInstance(new HashFunction(mapping.shaMode, mapping.shaDigestSize));
        }
