using System;
using System.Collections.Generic;
using System.Text;
using NIST.CVP.Crypto.AES;
using NIST.CVP.Crypto.AES_CCM;
using NIST.CVP.Crypto.CMAC.Enums;
using NIST.CVP.Crypto.KAS.Enums;
using NIST.CVP.Crypto.KAS.KC;
using NIST.CVP.Math;
using NIST.CVP.Tests.Core.TestCategoryAttributes;
using NUnit.Framework;

namespace NIST.CVP.Crypto.KAS.Tests.KC
{
    [TestFixture, UnitTest, FastIntegrationTest]
    public class KeyConfirmationAesCcmTests
    {
        private KeyConfirmationAesCcm _subject;

        private static object[] _tests = new object[]
        {
            new object[]
            {
                // keySize
                128,
                // tagLength
                128,
                // serverId
                new BitString("434156536964"),
                // iutId
                new BitString("a1b2c3d4e5"),
                // nonce
                new BitString("fe92de5495d53efa905c7dfb90"), 
                // serverPublicKey
                new BitString(""),
                // iutPublicKey
                new BitString(""),
                // derivedKeyingMaterial
                new BitString("0cf962452307c27f76a16a91c3c33e46"),
                // expectedMacData
                new BitString(""),
                // expectedTag
                new BitString("0239860fe82fe1723e17c00562d95876")
            },
            new object[]
            {
                // keySize
                192,
                // tagLength
                128,
                // serverId
                new BitString("434156536964"),
                // iutId
                new BitString("a1b2c3d4e5"),
                // nonce
                new BitString("81f1bdb3b5339f562ee18415e2"), 
                // serverPublicKey
                new BitString(""),
                // iutPublicKey
                new BitString(""),
                // derivedKeyingMaterial
                new BitString("e59516a95058b00207434fd25c1838c2f0153babefd3c945"),
                // expectedMacData
                new BitString(""),
                // expectedTag
                new BitString("9a6eada7f37edefc20bbf8b9d57b5df5")
            },
            new object[]
            {
                // keySize
                256,
                // tagLength
                128,
                // serverId
                new BitString("434156536964"),
                // iutId
                new BitString("a1b2c3d4e5"),
                // nonce
                new BitString("39ed699fad69afdb1135a432a7"), 
                // serverPublicKey
                new BitString(""),
                // iutPublicKey
                new BitString(""),
                // derivedKeyingMaterial
                new BitString("6caa18e3cbe837c449114e0e463214640422231e5208b67207528d548f4b1d7d"),
                // expectedMacData
                new BitString(""),
                // expectedTag
                new BitString("5ff8e6147a11d82357b6d113f4e11fc1")
            }
        };

        [Test]
        [TestCaseSource(nameof(_tests))]
        public void ShouldReturnCorrectMac(
            int keySize, int tagLength,
            BitString serverId, BitString iutId,
            BitString nonce,
            BitString serverPublicKey, BitString iutPublicKey,
            BitString derivedKeyingMaterial,
            BitString expectedMacData, BitString expectedTag)
        {
            var ccm = new AES_CCM.AES_CCM(new AES_CCMInternals(), new RijndaelFactory(new RijndaelInternals()));

            var p = new KeyConfirmationParametersAesCcm(
                KeyAgreementRole.UPartyInitiator,
                KeyConfirmationRole.Provider,
                KeyConfirmationDirection.Bilateral,
                KeyConfirmationMacType.AesCcm, // note this doesn't matter for the scope of this test
                keySize,
                tagLength,
                iutId,
                serverId,
                iutPublicKey,
                serverPublicKey,
                derivedKeyingMaterial,
                nonce
            );

            _subject = new KeyConfirmationAesCcm(ccm, p);

            var result = _subject.ComputeKeyMac();

            Assume.That(result.Success);
            Assert.AreEqual(expectedMacData.ToHex(), result.MacData.ToHex(), nameof(expectedMacData));
            Assert.AreEqual(expectedTag.ToHex(), result.Mac.ToHex(), nameof(expectedTag));
        }
    }
}
