        private readonly int[] _rates = new int[] {1152, 1088, 832, 576};

        [Test]
        [TestCase(224, "00", 0, "6b4e03423667dbb73b6e15454f0eb1abd4597f9a1b078e3f5b5a6bc7")]
        [TestCase(224, "3ecff375d2fde75a1f6117f3157d0e", 120, "7d17c454708bb568521ccb84c1053ffecf57b4b26d5db4ff8807e31f")]
        [TestCase(224, "4902", 10, "9fa09291f75f42f19783fcdb9959804e2339f06d2b85113b4184a5ce")]
        [TestCase(256, "00", 0, "a7ffc6f8bf1ed76651c14756a061d662f580ff4de43b49fa82d80a4b80f8434a")]
        [TestCase(256, "2507a10478b3ec", 56, "7ba62a503060ca2a82116550362405c09f495a299e7776616e308cfb534b37bc")]
        [TestCase(384, "00", 0, "0c63a75b845e4f7d01107d852e4c2485c51a50aaaa94fc61995e71bbee983a2ac3713831264adb47fb6bd1e058d5f004")]
        [TestCase(384, "4fd6d8c12ca67cdd", 64, "bd63c4b27647416428d5d6895ecbd35857517e436f804cd8f71d67ca0e1b47c40b4b62e4524be4ae41755ba9116fea61")]
        [TestCase(512, "00", 0, "a69f73cca23a9ac5c8b567dc185a756e97c982164fe25859e0d1dcc1475c80a615b2123af1f5f94c11e3e9402c3ac558f500199d95b6d3e301758586281dcd26")]
        [TestCase(512, "3e40", 16, "88d17f63e4ed51a6d00f8b09e682fe810e92acd31393834c61dbfb249345dba8f74a45791f7cc612f978b1297c1b33b13561b88d5c226a8872fafd95b55774b6")]
        public void ShouldPerformSpongeFunctionOnShortMessages(int digestSize, string inputHex, int length, string outputHex)
        {
            var message = new BitString(inputHex, length);
            var expectedResult = new BitString(outputHex);

            var result = KeccakInternals.Keccak(message, digestSize, digestSize * 2, false);

            Assert.AreEqual(expectedResult.ToHex(), result.ToHex());
        }

        [Test]
        [TestCase(224, 2312, "", "3cc4bfc29de1b0e58f7539a1ea1b1b100177b6416d62eeccc28f91ff")]
        [TestCase(256, 7664, "", "a7a833aa232873932a35fcea3f3ff1c9ad568f1b4b928b4d3ec54db55ec24da4")]
        [TestCase(384, 5032, "", "499efdf97e079e4fb481bc95654c938ea2b72282f078dfd92660ed1c91a068cf4445d629afb677f890e1e4eb6896fde9")]
        [TestCase(512, 8168, "", "30cf5936a6434d6560a21b500477b971cc64930da530a1188202af4f3b7d8e0e4dfb0459dff791710b795891388cdd5200afab664f26b3b9e7e977a8650a8025")]
        [TestCase(256, 4355, "", "2e4d78191c98d42926cb39e09d7f62938970139c519dd4d15c8ceead4e82cd06")]
        public void ShouldPerformSpongeFunctionOnLongMessages(int digestSize, int inputLen, string inputHex, string outputHex)
        {
            var message = new BitString(inputHex, inputLen);
            var expectedResult = new BitString(outputHex);

            var result = KeccakInternals.Keccak(message, digestSize, digestSize * 2, false);

            Assert.AreEqual(expectedResult.ToHex(), result.ToHex());
        }

        [Test]
        [TestCase(16, 256, 128, "7a4c48eb710299e4ff2be3f446327a6f", "f959")]
        [TestCase(80, 256, 128, "b13afc8f7e2b64ebf56613fe934783b4", "c60048b8ad8fc71e50ff")]
        [TestCase(819, 256, 128, "db928afb8d54fac61640e6366f77b897", "81758e600c555045302cd46f88397c918574d6e70bfabac206099bd61ae9d199237e6ca68b19caf29613a9bd207510afe93c83f8ba1c6f0ef5dbdc25f2073064e395acdb8ac2b96b4317936134419696732ef48c71baae5e857db0b7d34ca441a32c39b7a11400")]
        [TestCase(16, 512, 256, "4ac6f3faebf862e2de658d4c1e6a6d1f86c9cbd4414f533283b4ea5c76c2410e", "0689")]
        [TestCase(80, 512, 256, "cfb96c33aa8c056b378d461a13794bfbe4e79696e2c5b35697051be2e2945aaa", "96643f4e989cdcb1b7bc")]
        [TestCase(673, 512, 256, "2f17f54c6201f073e6c4f6ad94e846cd41f807f0e6218ab2fffcd0a230a74cf3", "06152ba4a6d3fc70031a3572204e35c473fa0975a60da27e3699168883136af80a0bacc0413785455790d0bc823eadc2ae6716d386ca79c3024b5e8621a1490e4709c706765c25b8920cbbb5b6a9da755524a24000")]
        public void ShouldPerformExtendableOutputFunction(int digestSize, int capacity, int inputLen, string inputHex, string outputHex)
        {
            var message = new BitString(inputHex, inputLen);
            var expectedResult = new BitString(outputHex, digestSize);

            var result = KeccakInternals.Keccak(message, digestSize, capacity, true);

            Assert.AreEqual(expectedResult.ToHex(), result.ToHex());
        }

        [Test]
        [TestCase(0)]
        [TestCase(100)]
        [TestCase(1605)]
        public void ShouldPerformPadFunctionForAllRates(int messageLength)
        {
            var randy = new Random800_90();
            var message = randy.GetRandomBitString(messageLength);

            foreach (var rate in _rates)
            {
                var result = KeccakInternals.PadMessage(message, rate);

                // Ensure the length of the resulting message is a positive multiple of the rate
                Assert.AreEqual(0, result.BitLength % rate);
            }
        }

