using NIST.CVP.Crypto.Common.Asymmetric.RSA.Enums;
using NIST.CVP.Crypto.Common.Asymmetric.RSA.Keys;
using NIST.CVP.Crypto.Common.Hash.ShaWrapper;
using NIST.CVP.Crypto.RSA.Signatures;
using NIST.CVP.Crypto.SHAWrapper;
using NIST.CVP.Math;
using NIST.CVP.Math.Entropy;
using NIST.CVP.Tests.Core.TestCategoryAttributes;
using NUnit.Framework;

namespace NIST.CVP.Crypto.RSA.Tests.Signatures
{
    [TestFixture, FastCryptoTest]
    public class FailingSignatureTests
    {
        [Test]
        [TestCase(SignatureSchemes.Ansx931, SignatureModifications.E, PssMaskTypes.None, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Ansx931, SignatureModifications.Message, PssMaskTypes.None, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Ansx931, SignatureModifications.ModifyTrailer, PssMaskTypes.None, "56ad21", "", "")]
        [TestCase(SignatureSchemes.Ansx931, SignatureModifications.MoveIr, PssMaskTypes.None, "a05095", "", "")]
        [TestCase(SignatureSchemes.Ansx931, SignatureModifications.Signature, PssMaskTypes.None, "0100000001", "", "")]

        [TestCase(SignatureSchemes.Pkcs1v15, SignatureModifications.E, PssMaskTypes.None, "1a16c9", "", "")]
        [TestCase(SignatureSchemes.Pkcs1v15, SignatureModifications.Message, PssMaskTypes.None, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pkcs1v15, SignatureModifications.ModifyTrailer, PssMaskTypes.None, "1a16c9", "", "")]
        [TestCase(SignatureSchemes.Pkcs1v15, SignatureModifications.MoveIr, PssMaskTypes.None, "c61d63", "", "")]
        [TestCase(SignatureSchemes.Pkcs1v15, SignatureModifications.Signature, PssMaskTypes.None, "0100000001", "", "")]

        [TestCase(SignatureSchemes.Pss, SignatureModifications.E, PssMaskTypes.MGF1, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.Message, PssMaskTypes.MGF1, "56ad21", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.ModifyTrailer, PssMaskTypes.MGF1, "a05095", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.MoveIr, PssMaskTypes.MGF1, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.Signature, PssMaskTypes.MGF1, "56ad21", "", "")]
        
        [TestCase(SignatureSchemes.Pss, SignatureModifications.E, PssMaskTypes.SHAKE128, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.Message, PssMaskTypes.SHAKE128, "56ad21", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.ModifyTrailer, PssMaskTypes.SHAKE128, "a05095", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.MoveIr, PssMaskTypes.SHAKE128, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.Signature, PssMaskTypes.SHAKE128, "56ad21", "", "")]
        
        [TestCase(SignatureSchemes.Pss, SignatureModifications.E, PssMaskTypes.SHAKE256, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.Message, PssMaskTypes.SHAKE256, "56ad21", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.ModifyTrailer, PssMaskTypes.SHAKE256, "a05095", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.MoveIr, PssMaskTypes.SHAKE256, "0100000001", "", "")]
        [TestCase(SignatureSchemes.Pss, SignatureModifications.Signature, PssMaskTypes.SHAKE256, "56ad21", "", "")]
        public void ShouldFailToVerify(SignatureSchemes sigScheme, SignatureModifications mods, PssMaskTypes pssMask, string eHex, string nHex, string dHex)
        {
            var rand = new Random800_90();

            var sigBuilder = new SignatureBuilder();
            var paddingFactory = new PaddingFactory(new MaskFactory(new ShaFactory()));
            var entropyProvider = new EntropyProvider(rand);
            var sha = new ShaFactory().GetShaInstance(new HashFunction(ModeValues.SHA2, DigestSizes.d256));
            var paddingScheme = paddingFactory.GetSigningPaddingScheme(sigScheme, sha, mods, pssMask, entropyProvider, 8);
            var rsa = new Rsa(new RsaVisitor());

            var message = entropyProvider.GetEntropy(1024);
            var e = new BitString(eHex).ToPositiveBigInteger();
            var n = new BitString(nHex).ToPositiveBigInteger();
            var d = new BitString(dHex).ToPositiveBigInteger();

            var publicKey = new PublicKey {E = e, N = n};
            var privateKey = new PrivateKey {D = d};

            var signature = sigBuilder
                .WithMessage(message)
                .WithPublicKey(publicKey)
                .WithPrivateKey(privateKey)
                .WithDecryptionScheme(rsa)
                .WithPaddingScheme(paddingScheme)
                .BuildSign();

            Assume.That(signature.Success, signature.ErrorMessage);

            var verify = sigBuilder
                .WithMessage(message)
                .WithPublicKey(publicKey)
                .WithDecryptionScheme(rsa)
                .WithPaddingScheme(paddingScheme)
                .BuildVerify();

            Assert.IsFalse(verify.Success);
        }
    }
}
