        #endregion VerifyTests
        public void ShouldVerifyCorrectly(ModeValues mode, DigestSizes dig, int nlen, int saltLen, string saltHex, string eHex, string nHex, string msgHex, string sigHex)
        {
            var shaFactory = new ShaFactory();
            var sha = shaFactory.GetShaInstance(new HashFunction(mode, dig));
            var signatureBuilder = new SignatureBuilder();
            
            var entropyProvider = new TestableEntropyProvider();
            var salt = new BitString(saltHex);
            entropyProvider.AddEntropy(salt);

            var maskFactory = new MaskFactory(shaFactory);
            var mask = maskFactory.GetMaskInstance(PssMaskTypes.MGF1, sha.HashFunction);

            var pssPaddingScheme = new PssPadder(sha, mask, entropyProvider, saltLen);
            
            var msg = new BitString(msgHex);
            var e = new BitString(eHex).ToPositiveBigInteger();
            var n = new BitString(nHex).ToPositiveBigInteger();
            var pubKey = new PublicKey {N = n, E = e};
            var sig = new BitString(sigHex);
            var rsa = new Rsa(new RsaVisitor());

            var result = signatureBuilder
                .WithMessage(msg)
                .WithSignature(sig)
                .WithPublicKey(pubKey)
                .WithDecryptionScheme(rsa)
                .WithPaddingScheme(pssPaddingScheme)
                .BuildVerify();

            Assert.IsTrue(result.Success, result.ErrorMessage);
        }

        [Test]
        [TestCase(ModeValues.SHA1, DigestSizes.d160, PssMaskTypes.MGF1, 2048, 4, "0100000001", "", "")]
        [TestCase(ModeValues.SHA2, DigestSizes.d224, PssMaskTypes.SHAKE128, 2048, 20, "1a16c9", "", "")]
        
        [TestCase(ModeValues.SHA2, DigestSizes.d256, PssMaskTypes.SHAKE128, 3072, 4, "0100000001", "", "")]
        [TestCase(ModeValues.SHA2, DigestSizes.d512, PssMaskTypes.MGF1, 3072, 64, "0100000001", "", "")]
        [TestCase(ModeValues.SHA2, DigestSizes.d512, PssMaskTypes.SHAKE256, 3072, 1, "0123456789", "", "")]
        
        [TestCase(ModeValues.SHA2, DigestSizes.d384, PssMaskTypes.SHAKE128, 4096, 4, "0100000001", "", "")]
        [TestCase(ModeValues.SHA3, DigestSizes.d512, PssMaskTypes.MGF1, 4096, 64, "0100000001", "", "")]
        [TestCase(ModeValues.SHA3, DigestSizes.d256, PssMaskTypes.SHAKE256, 4096, 16, "0100000001", "", "")]
        [TestCase(ModeValues.SHA2, DigestSizes.d256, PssMaskTypes.SHAKE256, 4096, 32, "0100000001", "", "")]
        public void ShouldCorrectlySignVerifyFips186_5MaskFunctions(ModeValues mode, DigestSizes dig, PssMaskTypes maskType, int nlen, int saltLen, string eHex, string dHex, string nHex)
        {
            var rand = new Random800_90();

            var shaFactory = new ShaFactory();
            var sha = shaFactory.GetShaInstance(new HashFunction(mode, dig));
            
            var signatureBuilder = new SignatureBuilder();
            
            var maskFactory = new MaskFactory(shaFactory);
            var mask = maskFactory.GetMaskInstance(maskType, sha.HashFunction);
            
            var e = new BitString(eHex).ToPositiveBigInteger();
            var n = new BitString(nHex).ToPositiveBigInteger();
            var d = new BitString(dHex).ToPositiveBigInteger();

            var key = new KeyPair {PubKey = new PublicKey {N = n, E = e}, PrivKey = new PrivateKey {D = d}};
            
            var rsa = new Rsa(new RsaVisitor());
            
            for (var i = 0; i < 10; i++)
            {
                var entropyProvider = new TestableEntropyProvider();
                var salt = rand.GetRandomBitString(saltLen * 8);
                entropyProvider.AddEntropy(salt);
            
                var pssPaddingScheme = new PssPadder(sha, mask, entropyProvider, saltLen);
                
                var msg = rand.GetRandomBitString(nlen - 1);

                var signature = signatureBuilder
                    .WithMessage(msg)
                    .WithKey(key)
                    .WithDecryptionScheme(rsa)
                    .WithPaddingScheme(pssPaddingScheme)
                    .BuildSign();

                Assume.That(signature.Success);

                var verify = signatureBuilder
                    .WithMessage(msg)
                    .WithKey(key)
                    .WithSignature(new BitString(signature.Signature))
                    .WithDecryptionScheme(rsa)
                    .WithPaddingScheme(pssPaddingScheme)
                    .BuildVerify();
                
                Assert.IsTrue(verify.Success, verify.ErrorMessage);
            }
        }
