        [TestCase(3072, ModeValues.SHA1, DigestSizes.d160, "0100000001", "9561c1db511e391ff04a9180c67a08bf775000b025bdae674d416f6b54753c46", "", "")]
        [TestCase(3072, ModeValues.SHA2, DigestSizes.d224, "0100000001", "cb7d720d1f179ba0cef363cd163174ea32af93e79f54ee296ca2d50713b9d6b9", "", "")]
        [TestCase(3072, ModeValues.SHA2, DigestSizes.d256, "0100000001", "3a844a987bad733ac4914d4e7f1f27fb3db71868d1f65b123f9f3c8e3dd51dc6", "", "")]
        [TestCase(3072, ModeValues.SHA2, DigestSizes.d384, "0100000001", "ad0c42e002efc105f2956a6b7899add6030a15f14aa8e066376e373636457162", "", "")]
        [TestCase(3072, ModeValues.SHA2, DigestSizes.d512, "0100000001", "596f67f2b589b3514d92104e397e39057c7138ea71754867b7e1f289e468f072", "", "")]
        [TestCase(3072, ModeValues.SHA2, DigestSizes.d256, "907121", "c69ea869dcd678859d6c07815cbf70322cf7d855c0f222b28905fb47eae3aa31", "", "")]
        // Suspected failing test
        //[TestCase(3072, ModeValues.SHA2, DigestSizes.d384, "5a789f", "637bba09d8f13a6338eb5ea2dc97a37de15ecc48afb2df926de48d239a657751", "", "")]
        public void ShouldPassWithGoodParameters(int nlen, ModeValues mode, DigestSizes dig, string e, string seed, string p, string q)
        {
            var sha = new ShaFactory().GetShaInstance(new HashFunction(mode, dig));
            var subject = new RandomProvablePrimeGenerator(sha);
            var result = subject.GeneratePrimes(nlen, new BitString(e).ToPositiveBigInteger(), new BitString(seed));
            Assert.IsTrue(result.Success, result.ErrorMessage);
            Assert.AreEqual(new BitString(p).ToPositiveBigInteger(), result.Primes.P, "p");
            Assert.AreEqual(new BitString(q).ToPositiveBigInteger(), result.Primes.Q, "q");
        }
    }
}
