using NIST.CVP.Crypto.Common.Hash.SHA3;
using NIST.CVP.Math;
using NIST.CVP.Tests.Core.TestCategoryAttributes;
using NUnit.Framework;

namespace NIST.CVP.Crypto.SHA3.Tests
{
    [TestFixture, FastCryptoTest]
    public class CSHAKETests
    {
        [Test]
        [TestCase(32, "00010203", "c1c36925b6409a04f1b504fcbca9d82b4017277cb5ed2b2065fc1d3814d5aaf5")]
        [TestCase(1600, "", "c5221d50e4f822d96a2e8881a961420f294b7b24fe3d2094baed2c6524cc166b")]
        public void ShouldCSHAKE128HashCorrectly(int length, string inputHex, string outputHex)
        {
            var message = new BitString(inputHex, length, false);
            var expectedResult = new BitString(outputHex);
            var hashFunction = GetCSHAKEHashFunction(256, 256);

            var subject = new CSHAKE();
            var result = subject.HashMessage(hashFunction, message, "", "Email Signature");

            Assume.That(result.Success);
            Assert.AreEqual(expectedResult, result.Digest);
        }

        [Test]
        [TestCase(32, "00010203", "d008828e2b80ac9d2218ffee1d070c48b8e4c87bff32c9699d5b6896eee0edd164020e2be0560858d9c00c037e34a96937c561a74c412bb4c746469527281c8c")]
        [TestCase(1600, "", "07dc27b11e51fbac75bc7b3c1d983e8b4b85fb1defaf218912ac86430273091727f42b17ed1df63e8ec118f04b23633c1dfb1574c8fb55cb45da8e25afb092bb")]
        public void ShouldCSHAKE256HashCorrectly(int length, string inputHex, string outputHex)
        {
            var message = new BitString(inputHex, length, false);
            var expectedResult = new BitString(outputHex);
            var hashFunction = GetCSHAKEHashFunction(512, 512);

            var subject = new CSHAKE();
            var result = subject.HashMessage(hashFunction, message, "", "Email Signature");

            Assume.That(result.Success);
            Assert.AreEqual(expectedResult, result.Digest);
        }

        [Test]
        [TestCase(9, "5301", "6df6ffae598a0629a6aef4ae140795b4")]
        [TestCase(811, "66c03e10e9de3487b90d0825d327c12495e3c89ad09c9d591e55c91376fb14c2fde9f7461fb25450df1a65806b65f3caf4d5c81ebc6e664871fcf915b9578bb70ee6776acc62205888dce2baa4024941209e81b4b35f0eda1bdcbd9ab1d6db6140bda4c41706", "447a80a502458e5aed5f270f88db81db")]
        [TestCase(2689, "", "db8c7d04878b9fdc78e9ff79982b71ca")]
        [TestCase(135844, "", "4e3c64509c08cd6a0cfe35aac76a47dc")]
        public void ShouldActAsSHAKE128WithNoCustomizationInput(int length, string inputHex, string outputHex)
        {
            var message = new BitString(inputHex, length, false);
            var expectedResult = new BitString(outputHex);
            var hashFunction = GetCSHAKEHashFunction(128, 256);

            var subject = new CSHAKE();
            var result = subject.HashMessage(hashFunction, message, "", "");

            Assume.That(result.Success);
            Assert.AreEqual(expectedResult, result.Digest);
        }

        [Test]
        [TestCase(11, "c907", "5714b276f46e92e62abe8230ca6f5d9f6d6cdce2a37af67747c1b0df82260f75")]
        [TestCase(825, "8b010d0e51a777fc3df97b479c67d68630f4906f6d98d454e8e3ffee0ce4f75a0e49be6124c747fcf2bd88174514d09604695b96646e24f55a8dfa1456cfd6db2efd6cfe5805007f5d9e7b758a2fbe0be9ed36cb7044b01d77796f0a708d7c46d2643e6e56274a00", "80c184bd6a641798f1069739cedc970c09b78cb0a88c165ef2f9c571e6d8b95b")]
        [TestCase(4355, "", "28765e7e3ae16f61aa4c340a2db373ab2e08ac5d647e9153653d371c54c88e72")]
        [TestCase(108899, "", "74228ea6f5357967499331aaf617385bff0e68b4cb7e0fab198e60aa2239eac2")]
        public void ShouldActAsSHAKE256WithNoCustomizationInput(int length, string inputHex, string outputHex)
        {
            var message = new BitString(inputHex, length, false);
            var expectedResult = new BitString(outputHex);
            var hashFunction = GetCSHAKEHashFunction(256, 512);

            var subject = new CSHAKE();
            var result = subject.HashMessage(hashFunction, message, "", "");

            Assume.That(result.Success);
            Assert.AreEqual(expectedResult, result.Digest);
        }

        private HashFunction GetCSHAKEHashFunction(int digestSize, int capacity)
        {
            return new HashFunction()
            {
                DigestSize = digestSize,
                Capacity = capacity,
                OutputType = Output.cXOF
            };
        }
    }

    
}
